import sqlite3conn = Nonedef create_table():    cursor = conn.cursor()    query = """ CREATE TABLE IF NOT EXISTS cat(num INTEGER, name TEXT,date_of_birth TEXT,father TEXT,mother TEXT,gender INTEGER,photo BLOB) """    cursor.execute(query)    conn.commit()    return "таблица создана"def init():    global conn    conn = sqlite3.connect('db/database.db')def db_sql():  passdef quit():    conn.close()